{"changed":true,"filter":false,"title":"ruby.rb","tooltip":"/ruby.rb","value":"# 番号を文字に変える関数\ndef janken(inp)\n\tif inp == 0\n\t\tdata = \"グー\"\n\telsif inp == 1\n\t\tdata = \"チョキ\"\n\telse\n\t\tdata = \"パー\"\n\tend\n\treturn data\nend\n\n# じゃんけんの勝ち負けを判定する関数\ndef win_or_lose_j(me,enemy)\n\tif me + 1 == enemy || (me == 2 && enemy == 0 )\n\t\treturn \"you_win\"\n\telsif me == enemy\n\t\treturn \"draw\"\n\telse\n\t\treturn \"you_lose\"\n\tend\t\t\nend\n\n# じゃんけんの結果\nclass Janken\n\tdef initialize(my_j_data,enemy_j_data)\n\t\t@me_n = my_j_data.to_i\n\t\t@en_n = enemy_j_data.to_i\n\t\t@me = janken(@me_n)\n\t\t@enemy = janken(@en_n)\n\tend\n\t\n\tdef result_j\n\t\tputs \"-------------------\"\n\t\tputs \"あなた：#{@me}を出しました\"\n\t\tputs \"相手：#{@enemy}を出しました\"\n\t\tputs \"-------------------\"\n\tend\n\t\n\tdef win_or_lose\n\t\twin_or_lose_result = win_or_lose_j(@me_n,@en_n)\n\t\treturn win_or_lose_result\n\tend\nend\n\n# あっち向いてホイを表す関数\ndef attimuitehoi(inp)\n\tif inp == 0\n\t\tdata = \"上\"\n\telsif inp == 1\n\t\tdata = \"下\"\n\telsif inp == 2\n\t\tdata = \"左\"\n\telse\n\t\tdata = \"右\"\n\tend\n\treturn data\nend\n\n# あっちむいてほいの結果\nclass Attimuitehoi\n\tdef initialize(my_a_data,enemy_a_data,win_or_lose_a)\n\t\t@me_n = my_a_data.to_i\n\t\t@en_n = enemy_a_data.to_i\n\t\t@me = attimuitehoi(@me_n)\n\t\t@enemy = attimuitehoi(@en_n)\n\t\t@wol = win_or_lose_a\n\tend\n\t\n\tdef result_a\n\t\tputs \"-------------------\"\n\t\tputs \"あなた：#{@me}\"\n\t\tputs \"相手：#{@enemy}\"\n\t\tif @me == @enemy\n\t\t\tif @wol == \"you_win\"\n\t\t\t\tputs \"あなたの勝ちです！\"\n\t\t\telse\n\t\t\t\tputs \"あなたの負けです！\"\n\t\t\tend\n\t\tend\n\tend\nend\n\nloop do\n\tputs \"じゃんけん...\"\n\tputs \"0(グー)1(チョキ)2(パー)3(戦わない)\"\n\tinput_j = gets.chomp\n\t\n\t# 入力した値を判別\n\tcase input_j\n\twhen \"0\",\"1\",\"2\"\n\t\tputs \"ホイ!\"\n\twhen \"3\"\n\t\tputs \"じゃんけんを終了します\"\n\t\tbreak\n\telse\n\t\tputs \"もう一度入力してください\"\n\t\tnext\n\tend\n\t\n\tjanken = Janken.new(input_j,rand(3))\n\tjanken.result_j()\n\t\n\tj_result = janken.win_or_lose()\n\t\n\t# あいこの処理\n\tif j_result == \"draw\"\n\t\tloop do\n\t\t\tputs \"あいこで...\"\n\t\t\tputs \"0(グー)1(チョキ)2(パー)3(戦わない)\"\n\t\t\tinput_j = gets.chomp;\n\t\t\tputs \"ショ!\"\n\t\t\tjanken = Janken.new(input_j,rand(3))\n\t\t\tjanken.result_j()\n\t\t\tj_result = janken.win_or_lose()\n\t\t\tif j_result = \"draw\"\n\t\t\t\tnext\n\t\t\telse\n\t\t\t\tbreak\n\t\t\tend\n\t\tend\n\tend\n\t\n\tloop do \n\t\tputs \"あっち向いて〜\"\n\t\tputs \"0(上)1(下)2(左)3(右)\"\n\t\tinput_a = gets.chomp\n\t\tcase input_a\n\t\twhen \"0\",\"1\",\"2\",\"3\"\n\t\t\tbreak\n\t\telse\n\t\t\tputs \"もう一度入力してください\"\n\t\t\tnext\n\t\tend\n\tend\n\t\n\tresult_attimuite = Attimuitehoi.new(input_a,rand(4),j_result)\n\tresult_attimuite.result_a\n\t\nend\n\n","undoManager":{"mark":-8,"position":100,"stack":[[{"start":{"row":119,"column":8},"end":{"row":119,"column":9},"action":"insert","lines":[" "],"id":1205}],[{"start":{"row":120,"column":0},"end":{"row":120,"column":1},"action":"insert","lines":["\t"],"id":1206},{"start":{"row":121,"column":0},"end":{"row":121,"column":1},"action":"insert","lines":["\t"]},{"start":{"row":122,"column":0},"end":{"row":122,"column":1},"action":"insert","lines":["\t"]}],[{"start":{"row":122,"column":22},"end":{"row":123,"column":0},"action":"insert","lines":["",""],"id":1207},{"start":{"row":123,"column":0},"end":{"row":123,"column":2},"action":"insert","lines":["\t\t"]}],[{"start":{"row":123,"column":2},"end":{"row":123,"column":3},"action":"insert","lines":["c"],"id":1208},{"start":{"row":123,"column":3},"end":{"row":123,"column":4},"action":"insert","lines":["a"]},{"start":{"row":123,"column":4},"end":{"row":123,"column":5},"action":"insert","lines":["s"]},{"start":{"row":123,"column":5},"end":{"row":123,"column":6},"action":"insert","lines":["e"]}],[{"start":{"row":123,"column":6},"end":{"row":123,"column":7},"action":"insert","lines":[" "],"id":1209},{"start":{"row":123,"column":7},"end":{"row":123,"column":8},"action":"insert","lines":["i"]},{"start":{"row":123,"column":8},"end